import React, { useState } from "react";
import P62Question from "./P62Question";
import P62InputFields from "./P62InputFields";
import "../62P.css";

const P62Theme = () => {
  const [selectedLeft, setSelectedLeft] = useState(null);

  const handleLeftSelect = (id) => {
    setSelectedLeft(id);
  };

  const handleConnectionChange = (connections) => {
    console.log("Current Connections:", connections);
  };

  return (
    <div className="question-grid">
      <P62Question onLeftSelect={handleLeftSelect} />
      <P62InputFields
        selectedLeft={selectedLeft}
        onConnectionChange={handleConnectionChange}
      />
    </div>
  );
};

export default P62Theme;
