import React, { useState } from "react";
import P61AnswerCheck from ".//P61AnswerCheck";
import { useNavigate } from "react-router-dom";

const P61InputFields = () => {
  const [answers, setAnswers] = useState({ 성주: "", 상구: "", 양심: "", 정선: "", friend1: "", friend2: "" });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAnswers((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    setIsSubmitted(true);
  };

  const handleNext = () => {
    navigate("/다음페이지");
  };

  return (
    <div>
      {!isSubmitted && (
        <>
          <h2>1. 네 친구들은 오늘 각각 몇 층으로 이동할 예정인가요?</h2>
          <div className="grid-container compact">
            {["성주", "상구", "양심", "정선"].map((name) => (
              <div className="input-group with-border" key={name}>
                <label className="label">{name}:</label>
                <input
                  type="text"
                  placeholder="0층"
                  name={name}
                  value={answers[name]}
                  onChange={handleChange}
                />
              </div>
            ))}
          </div>

          <h2>2. 내일 같은 층에서 만나게 될 친구 두 명은 누구인가요?</h2>
          <div className="inline-inputs">
            <b>친구1</b>
            <input
              type="text"
              placeholder="이름"
              name="friend1"
              value={answers.friend1}
              onChange={handleChange}
            />
            <b>친구2</b>
            <input
              type="text"
              placeholder="이름"
              name="friend2"
              value={answers.friend2}
              onChange={handleChange}
            />
          </div>

          <button className="next-button" onClick={handleSubmit}>
            제출
          </button>
        </>
      )}

      {isSubmitted && (
        <>
          <P61AnswerCheck answers={answers} />
          <button className="next-button" onClick={handleNext}>
            다음
          </button>
        </>
      )}
    </div>
  );
};

export default P61InputFields;
